import React, {memo} from "react";
import Header from "../../components/header/Header";
import Footer from "../../components/footer/Footer";
import TopButton from "../../components/topButton/TopButton";
// import FaqAccordion from "../../containers/faqAccordion/FaqAccordion";
import "./Faq.css";
import {FaqPageData} from "../../portfolio";
import {Fade} from "react-reveal";

const Faq = memo(({theme, onToggle}) => {
    const MemoizedExperienceAccordion = memo(FaqAccordion);

    return (
        <div className="faq-main">
            <Header theme={theme}/>
            <div className="basic-faq">
                <Fade bottom duration={2000} distance="40px">
                    <div className="heading-div">
                        <div className="heading-img-div">
                            <img
                                src={require("../../assests/images/faq.svg")}
                                alt="FAQ Image | Pouya Sadri"
                                className="heading-img"
                            />
                        </div>
                        <div className="heading-text-div">
                            <h1 className="heading-text" style={{color: theme.text}}>
                                Expert Web & Mobile Development FAQs - Your Questions Answered
                            </h1>
                            <h3 className="heading-sub-text" style={{color: theme.text}}>
                                Find answers to common queries about custom software solutions, project timelines, and
                                pricing. Dive into our FAQ section to learn how we can elevate your digital presence.
                            </h3>
                        </div>
                    </div>
                </Fade>
            </div>
            <hr/>
            <div className="basic-faq">
                <Fade bottom duration={2000} distance="40px">
                    <div className="faq-heading-div">
                        <div className="faq-heading-img-div">
                            <img
                                src={require("../../assests/images/faq.svg")}
                                alt="FAQ Image | Pouya Sadri"
                                className="heading-img"
                            />
                        </div>
                        <div className="faq-heading-text-div">
                            <h1
                                className="experience-heading-text"
                                style={{color: theme.text}}
                            >
                                {FaqPageData.title}
                            </h1>
                            <h3
                                className="experience-heading-sub-text"
                                style={{color: theme.text}}
                            >
                                {FaqPageData.subtitle}
                            </h3>
                            <p
                                className="experience-header-detail-text subTitle"
                                style={{color: theme.secondaryText}}
                            >
                                {FaqPageData.subtitle}
                            </p>
                        </div>
                    </div>
                </Fade>
            </div>
            {/*<MemoizedExperienceAccordion sections={experience["sections"]} theme={theme}/>*/}

            <Footer theme={theme} onToggle={onToggle}/>
            <TopButton theme={theme}/>
        </div>
    );
});

export default Faq;
